    def generatePrimes(self):
        self.prime_p = mathlib.generate_primes(n=512, k=1)[0]
        self.label_2.setText('P: {}'.format(self.prime_p))
        self.prime_q = mathlib.generate_primes(n=512, k=1)[0]
        self.label_3.setText('Q: {}'.format(self.prime_q))
        msgBox = QtWidgets.QMessageBox()
        msgBox.setWindowTitle('Тест на простоту')
        msgBox.setText("Каждое число прошло тест на простоту")
        msgBox.setStandardButtons(QtWidgets.QMessageBox.Ok)
        msgBox.exec()

    def generateKeys(self):
        self.publickey_e, self.publickey_n, self.privatekey_d = \
            rsa.RSA_keygen(512, self.prime_p, self.prime_q)
        if (self.bIsSender == True):
            self.label_5.setText('Открытый ключ: {}'.format(self.publickey_e))
            self.label_6.setText('N: {}'.format(self.publickey_n))
        else:
            self.label_8.setText('Открытый ключ: {}'.format(self.publickey_e))
            self.label_9.setText('N: {}'.format(self.publickey_n))

    def exchangeKeys(self):
        if (self.bIsSender == True):
            self.pPartner.label_5.setText(
                'Открытый ключ: {}'.format(self.publickey_e)
            )
            self.pPartner.label_6.setText(
                'N: {}'.format(self.publickey_n)
            )
        else:
            self.pPartner.label_8.setText(
                'Открытый ключ: {}'.format(self.publickey_e)
            )
            self.pPartner.label_9.setText('N: {}'.format(self.publickey_n))

    def encryptMessage(self):
        textToEncrypt = self.textBrowser.toPlainText()
        self.encryptedText = \
            rsa.RSA_encrypt(
                textToEncrypt,
                (self.pPartner.publickey_e, self.pPartner.publickey_n)
            )
        self.textBrowser.setText(' '.join(map(str, self.encryptedText)))

    def sendEncryptedMessage(self):
        self.pPartner.encryptedText = self.encryptedText
        self.pPartner.textBrowser.setText(' '.join(map(str, self.encryptedText)))

    def decryptMessage(self):
        self.textBrowser.setText(
            ''.join(
                map(
                    str,
                    rsa.RSA_decrypt(
                        self.encryptedText,
                        (self.privatekey_d, self.publickey_n)
                    )
                )
            )
        )


